const login = async (nic, password) => {
  try {
    const response = await axios.post('/auth/login', {
      nic: nic,
      password: password
    });

    localStorage.setItem('accessToken', response.data.accessToken);
    localStorage.setItem('refreshToken', response.data.refreshToken);
    localStorage.setItem('userRole', response.data.role);

    axios.defaults.headers.common['Authorization'] = `Bearer ${response.data.accessToken}`;

    return response.data;
  } catch (error) {
    console.error('Login error:', error.response?.data);
    throw error;
  }
};

export const useAuth = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const login = async (nic, password) => {
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post('/auth/login', { nic, password });

      localStorage.setItem('accessToken', response.data.accessToken);
      localStorage.setItem('userRole', response.data.role);

      return response.data;
    } catch (err) {
      const errorMsg = err.response?.data?.message || 'Login failed';
      setError(errorMsg);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  return { login, loading, error };
};

const createUser = async (userData) => {
  const token = localStorage.getItem('accessToken');

  try {
    const response = await axios.post('/auth/create-user', userData, {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      }
    });

    return response.data;
  } catch (error) {
    if (error.response?.data?.code === 'FORBIDDEN') {
      alert('Only administrators can create users');
    }
    throw error;
  }
};

axios.interceptors.response.use(
  response => response,
  async error => {
    const originalRequest = error.config;

    if (error.response?.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;

      try {
        const refreshToken = localStorage.getItem('refreshToken');
        const response = await axios.post('/auth/refresh', {
          refreshToken: refreshToken
        });

        localStorage.setItem('accessToken', response.data.accessToken);
        localStorage.setItem('refreshToken', response.data.refreshToken);

        axios.defaults.headers.common['Authorization'] = `Bearer ${response.data.accessToken}`;
        originalRequest.headers['Authorization'] = `Bearer ${response.data.accessToken}`;

        return axios(originalRequest);
      } catch (refreshError) {
        localStorage.clear();
        window.location.href = '/login';
        return Promise.reject(refreshError);
      }
    }

    return Promise.reject(error);
  }
);

export const PatientRegistrationForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    nic: '',
    address: '',
    phone: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const token = localStorage.getItem('accessToken');
      const response = await axios.post('/patient/register', formData, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      setSuccess(true);
      alert(`Patient registered! Patient ID: ${response.data.data.patientId}`);
      setFormData({ name: '', nic: '', address: '', phone: '' });
    } catch (err) {
      if (err.response?.data?.code === 'NIC_EXISTS') {
        setError('This NIC is already registered');
      } else if (err.response?.data?.code === 'VALIDATION_ERROR') {
        setError('Invalid NIC format');
      } else {
        setError(err.response?.data?.message || 'Registration failed');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Full Name"
        value={formData.name}
        onChange={(e) => setFormData({...formData, name: e.target.value})}
        required
      />
      <input
        type="text"
        placeholder="NIC"
        value={formData.nic}
        onChange={(e) => setFormData({...formData, nic: e.target.value})}
        required
      />
      <button type="submit" disabled={loading}>
        {loading ? 'Registering...' : 'Register Patient'}
      </button>
      {error && <div>{error}</div>}
      {success && <div>Registration successful!</div>}
    </form>
  );
};

export const usePatientSearch = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const searchPatient = async () => {
      if (!searchTerm.trim()) {
        setResults(null);
        return;
      }

      setLoading(true);
      setError(null);

      try {
        const token = localStorage.getItem('accessToken');
        const response = await axios.get(`/patient/search?nic=${encodeURIComponent(searchTerm)}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        setResults(response.data);
      } catch (err) {
        if (err.response?.status === 404) {
          setError('Patient not found');
        } else {
          setError(err.response?.data?.message || 'Search failed');
        }
        setResults(null);
      } finally {
        setLoading(false);
      }
    };

    const timeoutId = setTimeout(searchPatient, 500);
    return () => clearTimeout(timeoutId);
  }, [searchTerm]);

  return { searchTerm, setSearchTerm, results, loading, error };
};

export const CreateVisitForm = () => {
  const [patientId, setPatientId] = useState('');
  const [unit, setUnit] = useState('OPD');
  const [loading, setLoading] = useState(false);
  const [qrData, setQrData] = useState(null);

  const createVisit = async () => {
    if (!patientId) return;

    setLoading(true);

    try {
      const token = localStorage.getItem('accessToken');
      const response = await axios.post('/visit/create', {
        patientId,
        unit
      }, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      setQrData({
        token: response.data.qrToken,
        image: response.data.qrImage,
        visitId: response.data.visitId
      });

      setPatientId('');
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to create visit');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Patient ID (P-...)"
          value={patientId}
          onChange={(e) => setPatientId(e.target.value)}
        />
        <select value={unit} onChange={(e) => setUnit(e.target.value)}>
          <option value="OPD">OPD</option>
          <option value="SPECIAL">Special Unit</option>
        </select>
        <button onClick={createVisit} disabled={loading || !patientId}>
          {loading ? 'Creating...' : 'Create Visit'}
        </button>
      </div>

      {qrData && (
        <div>
          <h3>Visit Created: {qrData.visitId}</h3>
          <p>Scan this QR code:</p>
          <QRCode value={qrData.token} size={200} />
          <p>QR expires in 10 hours</p>
          <button onClick={() => navigator.clipboard.writeText(qrData.token)}>
            Copy QR Token
          </button>
        </div>
      )}
    </div>
  );
};

export const DoctorQRScanner = () => {
  const [scanning, setScanning] = useState(false);
  const [medicalHistory, setMedicalHistory] = useState([]);
  const [error, setError] = useState(null);

  const startScanner = async () => {
    const videoElement = document.getElementById('qr-video');
    if (!videoElement) return;

    const scanner = new QrScanner(
      videoElement,
      async (result) => {
        setScanning(false);
        scanner.stop();

        try {
          const accessToken = localStorage.getItem('accessToken');

          const response = await axios.get('/doctor/history', {
            headers: {
              'Authorization': `Bearer ${accessToken}`,
              'x-qr-token': result.data
            }
          });

          setMedicalHistory(response.data);
          setError(null);
        } catch (err) {
          if (err.response?.data?.code === 'QR_EXPIRED') {
            setError('QR code has expired');
          } else if (err.response?.data?.code === 'LICENSE_EXPIRED') {
            setError('Your medical license has expired');
          } else {
            setError(err.response?.data?.message || 'Failed to load medical history');
          }
        }
      },
      {
        highlightScanRegion: true,
        highlightCodeOutline: true
      }
    );

    await scanner.start();
    setScanning(true);
  };

  return {
    startScanner,
    scanning,
    medicalHistory,
    error
  };
};

export const MedicalRecordForm = ({ qrToken }) => {
  const [diagnosis, setDiagnosis] = useState('');
  const [prescriptions, setPrescriptions] = useState([{ name: '', dosage: '', frequency: '' }]);
  const [notes, setNotes] = useState('');
  const [submitting, setSubmitting] = useState(false);

  const addPrescription = () => {
    setPrescriptions([...prescriptions, { name: '', dosage: '', frequency: '' }]);
  };

  const updatePrescription = (index, field, value) => {
    const updated = [...prescriptions];
    updated[index][field] = value;
    setPrescriptions(updated);
  };

  const submitRecord = async () => {
    if (!diagnosis.trim()) {
      alert('Diagnosis is required');
      return;
    }

    setSubmitting(true);

    try {
      const accessToken = localStorage.getItem('accessToken');

      const response = await axios.post('/doctor/add-record', {
        diagnosis,
        prescriptions: prescriptions.filter(p => p.name.trim() && p.dosage.trim()),
        notes
      }, {
        headers: {
          'Authorization': `Bearer ${accessToken}`,
          'x-qr-token': qrToken
        }
      });

      alert(`Record saved. Prescription ID: ${response.data.prescriptionId || 'None'}`);

      setDiagnosis('');
      setPrescriptions([{ name: '', dosage: '', frequency: '' }]);
      setNotes('');
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to save record');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div>
      <h3>Add Medical Record</h3>

      <div>
        <label>Diagnosis *</label>
        <textarea
          value={diagnosis}
          onChange={(e) => setDiagnosis(e.target.value)}
          rows={3}
          required
        />
      </div>

      <div>
        <label>Prescriptions</label>
        {prescriptions.map((prescription, index) => (
          <div key={index}>
            <input
              placeholder="Medicine Name"
              value={prescription.name}
              onChange={(e) => updatePrescription(index, 'name', e.target.value)}
            />
            <input
              placeholder="Dosage"
              value={prescription.dosage}
              onChange={(e) => updatePrescription(index, 'dosage', e.target.value)}
            />
            <input
              placeholder="Frequency"
              value={prescription.frequency}
              onChange={(e) => updatePrescription(index, 'frequency', e.target.value)}
            />
          </div>
        ))}
        <button type="button" onClick={addPrescription}>
          + Add Another Medicine
        </button>
      </div>

      <div>
        <label>Notes</label>
        <textarea
          value={notes}
          onChange={(e) => setNotes(e.target.value)}
          rows={2}
        />
      </div>

      <button onClick={submitRecord} disabled={submitting || !diagnosis.trim()}>
        {submitting ? 'Saving...' : 'Save Medical Record'}
      </button>
    </div>
  );
};

export const PharmacyIssue = () => {
  const [scanning, setScanning] = useState(false);
  const [prescription, setPrescription] = useState(null);
  const [issuing, setIssuing] = useState(false);

  const startScanner = () => {
    const videoElement = document.getElementById('pharmacy-qr-video');
    const scanner = new QrScanner(videoElement, async (result) => {
      setScanning(false);
      scanner.stop();

      await getPrescriptionDetails(result.data);
    });

    scanner.start();
    setScanning(true);
  };

  const getPrescriptionDetails = async (qrToken) => {
    try {
      const accessToken = localStorage.getItem('accessToken');

      const response = await axios.get('/pharmacy/prescription/qr', {
        headers: {
          'Authorization': `Bearer ${accessToken}`,
          'x-qr-token': qrToken
        }
      });

      setPrescription({
        ...response.data,
        qrToken: qrToken
      });
    } catch (err) {
      alert(err.response?.data?.message || 'Failed to fetch prescription');
    }
  };

  const issuePrescription = async () => {
    if (!prescription) return;

    setIssuing(true);

    try {
      const accessToken = localStorage.getItem('accessToken');

      const response = await axios.post('/pharmacy/prescription/qr/issue', {}, {
        headers: {
          'Authorization': `Bearer ${accessToken}`,
          'x-qr-token': prescription.qrToken
        }
      });

      alert(`Prescription ${prescription.prescriptionId} issued!`);
      setPrescription(null);
    } catch (err) {
      if (err.response?.data?.code === 'ALREADY_ISSUED') {
        alert('This prescription was already issued');
      } else {
        alert(err.response?.data?.message || 'Failed to issue prescription');
      }
    } finally {
      setIssuing(false);
    }
  };

  return {
    startScanner,
    scanning,
    prescription,
    issuing,
    issuePrescription
  };
};

const checkSetup = async () => {
  try {
    const response = await axios.get('/setup/check');

    if (response.data.isFirstTimeSetup) {
      console.log('System needs first-time setup');
      showAdminSetupForm();
    } else {
      console.log(`System ready with ${response.data.userCount} users`);
      showLoginForm();
    }
  } catch (error) {
    console.error('Setup check failed:', error);
    alert('Cannot connect to server');
  }
};

checkSetup();
